var script_f34_7_34928=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function NasalO2FIO2_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.NasalO2FIO2_form){",
"",
"",
"doCalc = true;",
"if (Nasal_O2_param.value.indexOf(',') >= 0){ Nasal_O2_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Nasal_O2_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Nasal_O2_unit.options[Nasal_O2_unit.selectedIndex].value.split('|');",
"Nasal_O2 = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"FIO2 =  21 + (Nasal_O2 * 3);",
"",
"unit_parts = FIO2_unit.options[FIO2_unit.selectedIndex].value.split('|');",
"if (doCalc) FIO2_param.value = fixDP((FIO2 - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.NasalO2FIO2_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.NasalO2FIO2_form){",
"",
"FIO2_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
