" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Emergency complications of Hirschsprung disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?34/8/34950/contributors\">",
