var title_f34_8_34955="Revised diagnostic criteria CFS";
var content_f34_8_34955=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F55495&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F55495&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Revised CDC criteria for chronic fatigue syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A case of chronic fatigue syndrome is defined by the presence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Clinically evaluated, unexplained, persistent or relapsing",
"fatigue that is of new or definite onset; is not the result of ongoing",
"exertion; is not alleviated by rest; and results in substantial",
"reduction in previous levels of occupational, educational, social, or",
"personal activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        2. Four or more of the following symptoms that persist or recur",
"during six or more consecutive months of illness and that do not",
"predate the fatigue:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Self-reported impairment in short term memory or concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sore throat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Tender cervical or axillary nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Muscle pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Multijoint pain without redness or swelling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Headaches of a new pattern or severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Unrefreshing sleep",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Post-exertional malaise lasting &ge;24 hours",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    CDC: Centers for Disease Control and Prevention",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Fukuda, K, Straus, SE, Hickie, I, et al, Ann Intern Med 1994; 121:953.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
