" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical presentation and diagnosis of prostate cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/16/36101/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?35/16/36101/contributors\">",
"     Philip W Kantoff, MD",
"    </a>",
"    <br/>",
