" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of acute severe traumatic brain injury",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/2/35882/contributors\">",
