var title_f35_2_35884="Pediatric ambulance equipment for advanced life support";
var content_f35_2_35884=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F59854&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F59854&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pediatric equipment and supplies for advanced life support (ALS) ambulances in the United States",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       ALS ambulances",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Required*&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Airway and ventilation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Laryngoscope handle, extra batteries and bulbs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Laryngoscope blades: straight (Miller) sizes 0-4; curved (MacIntosh) sizes 2-4",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Endotracheal tubes: uncuffed sizes 2.5-5.5 mm; cuffed 6-8 mm (2 of each size and type)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Meconium aspirator adaptor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       10-mL non-Luerlock syringes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stylettes for endotracheal tubes: adult and pediatric",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Magill (Rovenstein) forceps, adult and pediatric",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Lubricating jelly (water soluble)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       End-tidal CO",
"       <sub>",
"        2",
"       </sub>",
"       detector (adult and pediatric colorimetric or quantitative capnometry)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Monitoring and defibrillation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Portable, battery-operated monitor/defibrillator with tape write-out/recorder, defibrillator pads, quick-look paddles or electrodes or hands-free patches, ECG leads, adult and pediatric chest attachment electrodes, adult and pediatric paddles",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Transcutaneous cardiac pacemaker with pediatric pads and cables (standalone or integrated into monitor/defibrillator)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Optional advanced equipment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Respirator (volume-cycled, on/off operation, 100 percent oxygen, 40-50 psi pressure (child/infant capabilities))",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pediatric blood sample tubes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Nasogastric tubes, pediatric feeding tube sizes 5 F and 8 F, sump tube sizes 8 F-16 F",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pediatric laryngoscope handles",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Size 1 curved (MacIntosh) laryngoscope blades",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       3.5 to 5.5 mm cuffed endotracheal tubes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Needle cricothyrotomy capability",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CO",
"     <sub>",
"      2",
"     </sub>",
"     : carbon dioxide; ECG: electrocardiogram; psi: pounds per square inch; F: French.",
"     <br>",
"      * In addition to the standard equipment and supplies as determined by state and local regulations.",
"      <br>",
"       &bull; In addition to items for basic life support ambulances.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Policy statement--Equipment for ambulances. American College of Surgeons Committee on Trauma, American College of Emergency Physicians, National Association of EMS Physicians, Pediatric Equipment Guidelines Committee - Emergency Medical Services for Children (EMSC) Partnership for Children Stakeholder Group, American Academy of Pediatrics. Pediatrics 2009; 124:e166.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
