var title_f35_22_36203="ART recommendations for eligible HIV-infected women";
var content_f35_22_36203=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F70652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F70652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antiretroviral treatment options recommended for HIV-infected pregnant women who are eligible for treatment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Maternal ART and infant ARV prophylaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Mother",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Maternal antepartum daily ART, starting as soon as possible irrespective of gestational age, and continued during pregnancy, delivery and thereafter. Recommended regimens include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        AZT + 3TC + NVP or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        AZT + 3TC + EFV* or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        TDF + 3TC (or FTC) + NVP or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        TDF + 3TC (or FTC) + EFV*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Infant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Daily NVP or twice-daily AZT from birth until 4 to 6 weeks of age (irrespective of the mode of infant feeding).",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AZT: zidovudine; 3TC: lamivudine; NVP: nevirapine; EFV: efavirenz; TDF: tenofovir; FTC: emtricitabine.",
"     <br>",
"      * Avoid use of EFV in the first trimester and use NVP instead.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: World Health Organization. Antiretroviral drugs for treating pregnant women and preventing HIV infection in infants: towards universal access: Recommendations for a public health approach (2010 version). World Health Organization, Geneva 2010. Copyright &copy; 2010 WHO. Available at:",
"     <a href=\"file://whqlibdoc.who.int/publications/2010/9789241599818_eng.pdf\">",
"      file://whqlibdoc.who.int/publications/2010/9789241599818_eng.pdf",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
