" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Tricyclic antidepressant poisoning",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
