" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prevention of recurrent gout",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/26/36266/contributors\">",
