var title_f35_26_36267="Diagnosis diabetic diarrhea";
var content_f35_26_36267=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F69401&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F69401&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Sequential evaluation of chronic diarrhea in patients with diabetes mellitus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        First line",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History and physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blood chemistry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stool analysis - weight, fat, chymotrypsin, elastase, leukocytes, parasites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Barium studies - looking for gastric retention, malabsorption pattern, small intestinal and colonic wall thickness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Second line",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper GI endoscopy with duodenal biopsy - histology, bacteriology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonoscopy with biopsy - histology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        [13]C-acetate or -octanoic acid breath test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Glucose hydrogen breath test - consider bacterial overgrowth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anorectal manometry and sensory testing if fecal incontinence is present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Third line",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ambulatory small intestinal manometry - to rule out intestinal pseudo-obstruction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        [75]Se-HCAT test or empiric cholestyramine - to rule out bile acid malabsorption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Push-enteroscopy with biopsy and enteroclysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Secretin-pancreazyme test - to exclude exocrine pancreatic insufficiency",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
