" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment and prevention of Q fever",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
