var title_f35_30_36333="Types of airway stents";
var content_f35_30_36333=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F58596%7EPULM%2F54642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F58596%7EPULM%2F54642&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Advantages and disadvantages of different airway stents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Silicone stents",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Easily inserted",
"        </p>",
"        <p>",
"         Repositioning and removal are easily accomplished",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Rigid bronchoscopy and general anesthesia required",
"        </p>",
"        <p>",
"         Stent migration is common",
"        </p>",
"        <p>",
"         Granulation tissue may obstruct proximal or distal ends of stent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Metal stents",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         May be introduced with fiberoptic scope without general anesthesia",
"        </p>",
"        <p>",
"         Self-expanding; resist extrinsic compression",
"        </p>",
"        <p>",
"         Minimal migration after placement",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Cannot be easily removed or repositioned",
"        </p>",
"        <p>",
"         Granulation tissue or tumor easily grows through spaces between metal struts",
"        </p>",
"        <p>",
"         Expansile force may rupture airways and vascular structures",
"        </p>",
"        <p>",
"         More expensive than silicone stents",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Hybrid stents",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Resist extrinsic compression",
"        </p>",
"        <p>",
"         Silicone membrane resists ingrowth of tumor or granulation tissue",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Cannot be easily removed or repositioned",
"        </p>",
"        <p>",
"         Most expensive",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Commercially-available airway stents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Brand",
"       </td>",
"       <td class=\"subtitle1\">",
"        Material",
"       </td>",
"       <td class=\"subtitle1\">",
"        Shapes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Specific problems/complications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dumon",
"       </td>",
"       <td>",
"        Silicone",
"       </td>",
"       <td>",
"        Straight, Y, J",
"       </td>",
"       <td>",
"        Migration in benign disease (straight)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Orlowski",
"       </td>",
"       <td>",
"        Silicone, armored with metal rings",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Very stiff",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hood",
"       </td>",
"       <td>",
"        Silicone",
"       </td>",
"       <td>",
"        Straight, Y",
"       </td>",
"       <td>",
"        Difficult to insert (Y)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gianturco",
"       </td>",
"       <td>",
"        Stainless steel, self-expandable",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Stent fracture, airway perforation, fatal hemoptysis,",
"occlusion by tissue growth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Strecker",
"       </td>",
"       <td>",
"        Tantalum, balloon-expandable",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Stent collapse, occlusion by tissue growth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Palmaz",
"       </td>",
"       <td>",
"        Stainless steel, balloon-expandable",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Stent collapse, occlusion by tissue growth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wallstent",
"       </td>",
"       <td>",
"        Cobalt-based alloy, uncovered, self-expandable",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Granuloma, occlusion by tissue growth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wallstent (Covered)",
"       </td>",
"       <td>",
"        Cobalt-based alloy, polyurethane covering,",
"self-expandable",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Granuloma at stent ends, airway perforation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ultraflex",
"       </td>",
"       <td>",
"        Nitinol, self-expandable covered and uncovered",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Granuloma formation, stent fracture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polyflex",
"       </td>",
"       <td>",
"        Polyester wire, silicone covering, self-expandable",
"       </td>",
"       <td>",
"        Straight, Y, J",
"       </td>",
"       <td>",
"        Migration (straight), difficult to insert (Y)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Screw-thread",
"       </td>",
"       <td>",
"        Tygon",
"       </td>",
"       <td>",
"        Straight",
"       </td>",
"       <td>",
"        Only tracheal sizes, mucus retention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dynamic",
"       </td>",
"       <td>",
"        Silicone, metal hoops",
"       </td>",
"       <td>",
"        Y",
"       </td>",
"       <td>",
"        Difficult to insert",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Montgomery",
"       </td>",
"       <td>",
"        Silicone",
"       </td>",
"       <td>",
"        T-tube",
"       </td>",
"       <td>",
"        Difficult to insert, tracheostomy necessary",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Manufacturer's details for all brands are as follows: Dumon: Endoxane&reg;, Novatech SA, Grasse, France; Orlowski: R&uuml;sch AG, Kernen, Germany; Hood: Hood Laboratories, Pembroke, MA, USA; Gianturco: Cook, Bjaeverskov, Denmark; Strecker: Microvasive-Boston Scientific, Natick, MA, USA; Palmaz: Corning/Johnson and Johnson, Warren, NJ, USA; Wallstent and Covered Wallstent: Microvasive-Boston Scientific; Ultraflex: Microvasive-Boston Scientific; Polyflex: R&uuml;sch AG; Screw-thread: Reynders Medical Supply, Lennik, Belgium; Dynamic: R&uuml;sch AG; Montgomery: Boston Medical Products, Inc., Waltham, MA, USA.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Bolliger, CT, Mathur, PN, Beamis, JF, et al. Eur Respir J 2002; 19:365. Copyright &copy; 2002 European Respiratory Society Journals Ltd.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
