var title_f35_32_36363="Cardiovascular autonomic tests";
var content_f35_32_36363=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F57628&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F57628&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cardiovascular autonomic tests and suggested indications for their use",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cardiovascular autonomic tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Utility for clinical diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiovascular tests of heart rate response",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Orthostatic hypotension test",
"       </td>",
"       <td>",
"        Yes, but low sensitivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        QT interval",
"       </td>",
"       <td>",
"        Yes, for additional information and risk stratification, but low sensitivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ambulatory blood pressure monitoring for dipping status",
"       </td>",
"       <td>",
"        Yes, for risk stratification, but low sensitivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heart rate variability time and frequency domain indices",
"       </td>",
"       <td>",
"        Yes, for early additional information and risk stratification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac vagal baroreflex sensitivity measures",
"       </td>",
"       <td>",
"        No, provides early additional information and risk stratification, but limited availability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scintigraphic imaging",
"       </td>",
"       <td>",
"        No, limited availability and limited standardization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Muscle sympathetic nerve activity",
"       </td>",
"       <td>",
"        No, limited availability and limited data in cardiac autonomic neuropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Catecholamine assessment",
"       </td>",
"       <td>",
"        No, limited availability",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     <p>",
"      &nbsp;",
"     </p>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Tesfaye S, Boulton AJ, Dyck PJ, et al. Diabetic neuropathies: update on definitions, diagnostic criteria, estimation of severity, and treatments. Diabetes Care 2010; 33:2285.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
