" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of ankle fractures in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
