" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Radiologic evaluation of the painful shoulder",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/36/36425/contributors\">",
