"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       System",
"      </td>",
"      <td class=\"subtitle1\">",
"       Skin types*",
"      </td>",
"      <td class=\"subtitle1\">",
"       Advantages",
"      </td>",
"      <td class=\"subtitle1\">",
"       Disadvantages",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1064 nm Nd-YAG",
"      </td>",
"      <td>",
"       I-VI",
"      </td>",
"      <td>",
"       Good for darker skin types",
"      </td>",
