var title_f35_37_36444="Rx for hepatic fibrosis";
var content_f35_37_36444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F50384&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F50384&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Therapeutic strategies for hepatic fibrosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Strategies",
"       </td>",
"       <td class=\"subtitle1\">",
"        Approach",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"6\">",
"        Remove injurious stimuli",
"       </td>",
"       <td>",
"        Abstinence from alcohol for alcoholic liver diseases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antiviral therapy for viral hepatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antihelminthic therapy for schistosomiasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Copper chelation for Wilson's disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phlebotomy for hemochromatosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discontinue hepatotoxins (eg, methotrexate) in drug-induced liver injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"4\">",
"        Suppress hepatic inflammation",
"       </td>",
"       <td>",
"        Corticosteroids in autoimmune and alcoholic liver diseases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neutralizing inflammatory cytokines using specific receptor antagonists: IL-1 receptor antagonists, soluble TNF-alpha receptor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ursodeoxycholic acid (UDCA)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Others: prostaglandin E, colchicine and colchiceine, milotilate, translast, IL-10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"14\">",
"        Downregulate stellate cell activation",
"       </td>",
"       <td>",
"        Gamma Interferon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antioxidants: alpha-tocopherol, resveratrol, quercetin, N-acetylcysteine, silymarin, OC-15161",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Cytokine-directed therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        TGF-beta antagonists",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Endothelin receptor antagonists",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hepatic growth factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disrupt ECM-HSC interactions: Arg-Gly-Asp (RGD) analogue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Collagen synthesis inhibitors:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        TGF-beta antagonists, relaxin, halofuginone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Others:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dilinoleylphosphotidylcholine (DLPC), HMG CoA reductase,",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pentoxyphylline, HOE077, Safironil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Retinoids?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Herbal medicine: Sho-saiko-to (Xiao-Chaihu-Tang), Salvia miltiorrhiza (Dan-Shen)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Promote matrix degradation",
"       </td>",
"       <td>",
"        Target degradation of interstitial-type matrix, rather than basement membrane-type matrix",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TGF-beta antagonists, relaxin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Promote specific apoptosis of hepatic stellate cells",
"       </td>",
"       <td>",
"        Not yet available",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
