" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Dynamic hyperinflation in patients with COPD",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/38/36454/contributors\">",
