" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cochlear implant infections",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/4/35913/contributors\">",
