var title_f35_4_35916="Autism screening tools";
var content_f35_4_35916=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F71355&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F71355&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Autism-specific screening tools for young children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Tool",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sensitivity/specificity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Validation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CHAT",
"       </td>",
"       <td>",
"        18-24 months",
"       </td>",
"       <td>",
"        <p>",
"         9 parent-report items",
"        </p>",
"        <p>",
"         5 observed items",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Sensitivity: 20 to 38 percent",
"        </p>",
"        <p>",
"         Specificity: 98 percent",
"        </p>",
"       </td>",
"       <td>",
"        &gt;16,000 children in the community",
"       </td>",
"       <td>",
"        Validated as first-stage screen, but may be more useful as a second-stage screen.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        M-CHAT",
"       </td>",
"       <td>",
"        16-30 months",
"       </td>",
"       <td>",
"        <p>",
"         23 parent-report items",
"        </p>",
"        <p>",
"         Takes approximately 5 minutes to administer",
"        </p>",
"        <p>",
"         Available in English, Spanish, and other languages (see",
"         <a href=\"file://www2.gsu.edu/~psydlr/Diana_L._Robins,_Ph.D..html\" target=\"_blank\">",
"          file://www2.gsu.edu/~psydlr/Diana_L._Robins,_Ph.D..html",
"         </a>",
"         )",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Sensitivity: 85 percent",
"        </p>",
"        <p>",
"         Specificity: 93 percent",
"        </p>",
"        <p>",
"         Positive predictive value 57 to 76 percent when followed by structured interview (approximately 60 percent when used as first-stage screen)",
"        </p>",
"       </td>",
"       <td>",
"        &gt;5000 children in primary care practices",
"       </td>",
"       <td>",
"        Validated as first-stage screen. Failed screen is followed by structured interview before referral for diagnostic evaluation. (See text for details).",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        STAT",
"       </td>",
"       <td>",
"        24-36 months",
"       </td>",
"       <td>",
"        <p>",
"         12 observed activities during 20-minute play session",
"        </p>",
"        <p>",
"         Requires training for administration and scoring",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Sensitivity: 92 percent",
"        </p>",
"        <p>",
"         Specificity: 85 percent",
"        </p>",
"       </td>",
"       <td>",
"        52 children with ASD and other developmental disorders",
"       </td>",
"       <td>",
"        <p>",
"         Not suitable for well-child screening, but is an alternative for second-stage screening.",
"        </p>",
"        <p>",
"         Language comprehension is not required. Requires training for administration and scoring; not suitable for primary care practice.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        CSBC-DP-ITC",
"       </td>",
"       <td>",
"        6-24 months",
"       </td>",
"       <td>",
"        24-item questionnaire",
"       </td>",
"       <td>",
"        Positive predictive value: 75 percent",
"       </td>",
"       <td>",
"        10,479 infants screened at 1-year health supervision visit",
"       </td>",
"       <td>",
"        The CSBC-DP-ITC is a broadband screen for communication delays.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CHAT: Checklist for Autism in Toddlers; M-CHAT: Modified CHAT; STAT: Screening Tool for Autism in Two-Year-Olds; SCQ: Social Communication Questionnaire (formerly the Autism Screening Questionnaire); CSBC-DP-ITC: Communication and Symbolic Behavior Scales Developmental Profile &ndash; Infant Toddler Checklist.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
