var title_f35_42_36524="Atypical Spitz histo";
var content_f35_42_36524=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F85815&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F85815&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic features of atypical Spitz tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Architecture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Diameter usually &gt;5 and often &gt;10 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Involvement of the subcutaneous tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Ulceration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Poor circumscription",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Extensive pagetoid scatter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Effacement of the epidermis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Prominent confluence of melanocytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; High cellular density",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Lack of zonation and maturation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Asymmetry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Absent or few eosinophilic hyaline globules (Kamino bodies)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Proliferation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Significant mitotic rate (2 to 6/mm",
"        <sup>",
"         2",
"        </sup>",
"        ) (particularly &gt;6/mm",
"        <sup>",
"         2",
"        </sup>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Deep or marginal dermal mitoses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Proliferative index (Ki-67 expression)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        - Between 2 to 10 percent",
"        <sup>",
"         [1]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        - &ge;10 to 15 percent",
"        <sup>",
"         [2]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cytology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Granular versus ground glass cytoplasm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; High nuclear to cytoplasmic ratio",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Loss of delicate or dispersed chromatin pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Thickening of nuclear membranes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Hyperchromatism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &bull; Large nucleoli",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Vollmer RT. Use of Bayes rule and MIB-1 proliferation index to discriminate Spitz nevus from malignant melanoma. Am J Clin Pathol 2004; 122:499.",
"       </li>",
"       <li>",
"        Kapur P, Selim MA, Roy LC, et al. Spitz nevi and atypical Spitz nevi/tumors: a histologic and immunohistochemical analysis. Mod Pathol 2005; 18:197.",
"       </li>",
"      </ol>",
"      Reprinted by permission from Macmillan Publishers Ltd: Modern Pathology. Barnhill RL. The Spitzoid lesion: Rethinking spitz tumors, atypical variants, 'Spitzoid melanoma' and risk assessment. Mod Pathol 2006; 19 Suppl 2:S21. Copyright &copy; 2006.",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
