" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"drugTitle\">",
"   Sodium phosphate: Pediatric drug information",
"  </div>",
"  <div id=\"lexiTitleImg\">",
"   <img height=\"17\" src=\"./../images/lexiComp/Lexicomp_2012_71x17.myextg\" width=\"71\"/>",
