" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Hematologic manifestations of rheumatoid arthritis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/48/36616/contributors\">",
