" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of urethral stricture disease in men",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/48/36617/contributors\">",
