var title_f35_51_36667="Catastrophic APS criteria";
var content_f35_51_36667=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F62724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F62724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Preliminary criteria for the classification of the catastrophic anti-phospholipid antibody syndrome (APS)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Evidence of involvement of three or more organs, systems and/or tissues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Development of manifestations simultaneously or in less than a week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. Confirmation by histopathology of small vessel occlusion in at least one organ or tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. Laboratory confirmation of the presence of",
"antiphospholipid antibodies (lupus anticoagulant and/or anticardiolipin",
"antibodies)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Classification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Definite catastrophic APS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Requires all four criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Probable catastrophic APS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All four criteria, except for only two organs, systems, and/or sites of tissue involvement",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All four criteria, except for the laboratory confirmation at least",
"six weeks apart due to the early death of a patient never tested for",
"APL before the catastrophic APS",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Criteria 1, 2, and 4 above",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1, 3, and 4 and the development of a third event in more than a week but less than a month, despite anticoagulation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Asherson, RA, Cervera, R, de Groot, PG, et al. Lupus 2003; 12:530.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
