var title_f35_53_36700="Algorithm genet couns refer BRCA";
var content_f35_53_36700=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F51428&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F51428&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Algorithm for genetic counseling referral from the Clinical Cancer Genetics Program at Ohio State University",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Non-Jewish families",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        High risk* breast-ovarian; any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of breast cancer &le;40-years-old in a first or second-degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One first or second-degree relative with both breast and ovarian cancer at any age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Two cases of breast cancer in first or second-degree relatives if one is diagnosed at &le;50 years of age or is bilateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One first or second-degree relative with breast cancer diagnosed at &le;50-years-old or bilateral",
"        <strong>",
"         and",
"        </strong>",
"        one first or second-degree relative with ovarian cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Three cases of breast and ovarian cancer (at least one case of ovarian cancer) in first or second-degree relatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Two cases of ovarian cancer in first or second-degree relatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of male breast cancer in a first or second-degree relative if another first or second degree relative has (male or female) breast or ovarian cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Moderate risk* breast; any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Two first-degree relatives if both diagnosed between 51 and 60 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One first degree and one second-degree relative (mother or sister and maternal aunt or maternal grandmother), if the sum of their ages is &le;118-years-old",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Moderate risk ovarian:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One first-degree relative with ovarian cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Jewish families",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        High risk* breast-ovarian; any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of breast cancer &le;50-years-old in a first or second-degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of ovarian cancer at any age in a first or second-degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A first or second degree relative with breast cancer at any age if another first or second degree relative has breast and/or ovarian cancer at any age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of male breast cancer in a first or second-degree relative",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * High risk: Family histories suggestive of a hereditary cancer susceptibility syndrome where the individual would benefit from referral to a cancer genetics professional and increased cancer surveillance; Moderate risk: Family histories not diagnostic of a hereditary cancer susceptibility syndrome but conferring an increased risk for cancer, requiring increased cancer surveillance.",
"    </div>",
"    <div class=\"reference\">",
"     Hampel H, et al. J Med Genet 2004; 41:81.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
