var script_f35_55_36722=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function PostTestProb_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.PostTestProb_form){",
"",
"",
"doCalc = true;",
"if (Pre_Test_Prob_param.value.indexOf(',') >= 0){ Pre_Test_Prob_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Pre_Test_Prob_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Pre_Test_Prob_unit.options[Pre_Test_Prob_unit.selectedIndex].value.split('|');",
"Pre_Test_Prob = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Sensitivity_param.value.indexOf(',') >= 0){ Sensitivity_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Sensitivity_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Sensitivity_unit.options[Sensitivity_unit.selectedIndex].value.split('|');",
"Sensitivity = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Specificity_param.value.indexOf(',') >= 0){ Specificity_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Specificity_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Specificity_unit.options[Specificity_unit.selectedIndex].value.split('|');",
"Specificity = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Post_Odds_Pos =  (Pre_Test_Prob * Sensitivity) / ((1 - Pre_Test_Prob) * (1-Specificity));",
"",
"Post_Test_Prob_Pos_Result =  Post_Odds_Pos / (1 + Post_Odds_Pos);",
"",
"unit_parts = Post_Test_Prob_Pos_Result_unit.options[Post_Test_Prob_Pos_Result_unit.selectedIndex].value.split('|');",
"if (doCalc) Post_Test_Prob_Pos_Result_param.value = fixDP((Post_Test_Prob_Pos_Result - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"Post_Odds_Neg =  (Pre_Test_Prob * (1-Sensitivity)) / ((1-Pre_Test_Prob) * Specificity);",
"",
"Post_Test_Prob_Neg_Result =  Post_Odds_Neg / (1 + Post_Odds_Neg);",
"",
"unit_parts = Post_Test_Prob_Neg_Result_unit.options[Post_Test_Prob_Neg_Result_unit.selectedIndex].value.split('|');",
"if (doCalc) Post_Test_Prob_Neg_Result_param.value = fixDP((Post_Test_Prob_Neg_Result - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PostTestProb_form){",
"",
"if (Pre_Test_Prob_param.value && Pre_Test_Prob < 0) {",
"Pre_Test_Prob = 0;",
"Pre_Test_Prob_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Pre Test Prob is 0 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Pre_Test_Prob_param.value && Pre_Test_Prob > 1) {",
"Pre_Test_Prob_param.value = \"\";",
"clrResults();",
"Pre_Test_Prob = 0;",
"doCalc = false;",
"alert(\"The maximum value for Pre Test Prob is 1 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Sensitivity_param.value && Sensitivity < 0) {",
"Sensitivity = 0;",
"Sensitivity_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Sensitivity is 0 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Sensitivity_param.value && Sensitivity > 1) {",
"Sensitivity_param.value = \"\";",
"clrResults();",
"Sensitivity = 0;",
"doCalc = false;",
"alert(\"The maximum value for Sensitivity is 1 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Specificity_param.value && Specificity < 0) {",
"Specificity = 0;",
"Specificity_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Specificity is 0 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Specificity_param.value && Specificity > 1) {",
"Specificity_param.value = \"\";",
"clrResults();",
"Specificity = 0;",
"doCalc = false;",
"alert(\"The maximum value for Specificity is 1 fraction.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PostTestProb_form){",
"",
"Post_Test_Prob_Pos_Result_param.value = '';",
"Post_Test_Prob_Neg_Result_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
