var title_f35_55_36731="Antithyroid drug controversies";
var content_f35_55_36731=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F52180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F52180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Controversies about antithyroid drug use",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Controversy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Best available answer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For how long should patients be treated?",
"       </td>",
"       <td>",
"        12-18 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the dose influence the chances of remission?",
"       </td>",
"       <td>",
"        Probably not",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do higher doses bring the disease under more rapid control?",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does the coadministration or subsequent use of T4 or T3 improve remission rates?",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are there baseline predictors of the likelihood of remission?",
"       </td>",
"       <td>",
"        Severe disease, large goiter, high anti-TSH receptor antibody titers may be predictive of failure, but only in extreme cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does pretreatment with antithyroid drugs decrease the chances",
"of thyroid storm or other adverse outcomes after radioiodine therapy?",
"       </td>",
"       <td>",
"        Possibly, but only because thyroid hormone levels are lower after therapy in those few patients who exhibit an exacerbation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Will antithyroid drug therapy after radioiodine lead to more rapid disease control?",
"       </td>",
"       <td>",
"        PTU, no (at 6 wk); methimazole, yes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Cooper DC. Antithyroid drugs in the management of patients with Graves' disease: An evidence-based approach to therapeutic controversies. J Clin Endocrinol Metab 2003; 88:3474. file://jcem.endojournals.org/. Copyright &copy; 2003 The Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
