" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Amanita smithiana mushroom poisoning",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?35/57/36759/contributors\">",
