var title_f35_57_36765="Rx serious VISA child";
var content_f35_57_36765=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F69174&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F69174&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Parenteral antimicrobial regimens for definitive treatment of bacteremia and other serious vancomycin-intermediately susceptible Staphylococcus aureus infections* in children older than 30 days",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antimicrobial regimen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Regimens of choice",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        <p>",
"         Linezolid",
"         <sup>",
"          &Delta;",
"         </sup>",
"         &plusmn;",
"        </p>",
"        <p>",
"         Gentamicin (or &plusmn; rifampin)",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Linezolid: &lt;12 years: 30 mg/kg per day in 3 doses; &ge;12 years: 600 mg twice per day",
"        </p>",
"        <p>",
"         Gentamicin: 3 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         Rifampin: 20 mg/kg per day in 2 doses; maximum daily dose 600 mg",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Daptomycin",
"         <sup>",
"          &Delta;&sect;",
"         </sup>",
"         &plusmn;",
"        </p>",
"        <p>",
"         Gentamicin (or &plusmn; rifampin)&loz;",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Daptomycin: 9 mg/kg",
"         <sup>",
"          &yen;",
"         </sup>",
"         per day in 1 dose",
"        </p>",
"        <p>",
"         Gentamicin: 3 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         Rifampin: 20 mg/kg per day in 2 doses; maximum daily dose 600 mg",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Quinupristin-dalfopristin",
"         <sup>",
"          &Delta;",
"         </sup>",
"         &plusmn;",
"        </p>",
"        <p>",
"         Gentamicin (or &plusmn; rifampin)",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Quinupristin-dalfopristin: 22.5 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         Gentamicin: 3 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         Rifampin: 20 mg/kg per day in 2 doses; maximum daily dose 600 mg",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        Alternative regimens",
"       </td>",
"       <td>",
"        <p>",
"         Vancomycin +",
"        </p>",
"        <p>",
"         Linezolid",
"         <sup>",
"          &Delta;",
"         </sup>",
"         &plusmn;",
"        </p>",
"        <p>",
"         Gentamicin",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Vancomycin: 60 mg/kg per day in 4 doses; maximum daily dose 4 g",
"         <sup>",
"          ������",
"         </sup>",
"        </p>",
"        <p>",
"         Linezolid: &lt;12 years: 30 mg/kg per day in 3 doses; &ge;12 years: 600 mg twice per day",
"        </p>",
"        <p>",
"         Gentamicin: 3 mg/kg per day in 3 doses",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Vancomycin +",
"        </p>",
"        <p>",
"         Trimethoprim-sulfamethoxazole (TMP-SMX)",
"         <sup>",
"          &Delta;",
"         </sup>",
"         &plusmn;",
"        </p>",
"        <p>",
"         Gentamicin (or &plusmn; rifampin)",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Vancomycin: 60 mg/kg per day in 4 doses; maximum daily dose 4 g",
"         <sup>",
"          ������",
"         </sup>",
"        </p>",
"        <p>",
"         TMP-SMX: 8-12 mg/kg trimethoprim/40 to 60 mg/kg sulfamethoxazole per day in 4 doses",
"        </p>",
"        <p>",
"         Gentamicin: 3 mg/kg per day in 3 doses",
"        </p>",
"        <p>",
"         Rifampin: 20 mg/kg per day in 2 doses; maximum daily dose 600 mg",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cilndamycin",
"        <sup>",
"         ������",
"        </sup>",
"        (if strain susceptible and D-test negative)",
"       </td>",
"       <td>",
"        40 mg/kg per day in 3 to 4 doses; maximum daily dose 1.2 to 2.7 g",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * MIC &gt;4 mcg/mL and &le;16 mcg/mL.",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      Optimal therapy is not known; dependent on in vitro susceptibility results.",
"      <br>",
"       &Delta; Consultation with an expert in infectious diseases is suggested before use.",
"       <br>",
"        <font class=\"lozenge\">",
"         &loz;",
"        </font>",
"        Gentamicin or rifampin may be added for life-threatening infections, such as endocarditis or central nervous system, although there are no data from controlled trials to indicate that combination therapy is beneficial. Consultation with an expert in infectious diseases may be warranted to determine which agent to use and duration of use.",
"        <br>",
"         &sect; Daptomycin is active in vitro against multidrug-resistant gram-positive organisms, including",
"         <em>",
"          Staphylococcus aureus",
"         </em>",
"         , but is not well studied in children. It should not be used alone for the treatment of pulmonary infections. There is no experience with daptomycin in the treatment of central nervous system infections. Daptomycin is approved by the United States Food and Drug Administration only for the treatment of complicated skin and skin-structure infections in patients &ge;18 years of age.",
"         <br>",
"          &yen; May need to increase dose to 10 to 12 mg/kg per day to reach concentrations efficacious in adults.",
"          <br>",
"           &Dagger; Goal is to achieve trough levels &gt;15 to 20 mcg/mL based on adult studies.",
"           <br>",
"            &dagger; Once bacteremia is cleared.",
"           </br>",
"          </br>",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Staphylococcal infections. In: Red Book: 2012 Report of the Committee on Infectious Diseases, 29",
"        <sup>",
"         th",
"        </sup>",
"        ed, Pickering LK (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2012. p.653.",
"       </li>",
"       <li>",
"        American Academy of Pediatrics. Tables of antibacterial drugs. In: Red Book: 2012 Report of the Committee on Infectious Diseases, 29",
"        <sup>",
"         th",
"        </sup>",
"        ed, Pickering LK (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2012. p.807.",
"       </li>",
"       <li>",
"        Liu C, Bayer A, Cosgrove SE, et, al. Clinical practice guidelines by the Infectious Diseases Society of America for the treatment of methicillin-resistant Staphylococcus aureus infections in adults and children. Clin Infect Dis 2011; 52:e18.",
"       </li>",
"       <li>",
"        Safety and Efficacy Study of Daptomycin in Pediatric Subjects (2-17 Years) With Skin and Skin Structure Infections. Available at:",
"        <a href=\"file://clinicaltrials.gov/ct2/show/NCT00711802\">",
"         clinicaltrials.gov/ct2/show/NCT00711802",
"        </a>",
"        (Accessed on August 2, 2012).",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
