" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 551px\">",
"   <div class=\"ttl\">",
"    Physical examination maneuver for osteitis pubis",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 531px; height: 451px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
