var title_f35_60_36815="Back stretch";
var content_f35_60_36815=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F50339&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F50339&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Flexibility exercise: Back",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 369px; height: 297px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    This exercise is for your back muscles. If you've had hip or back surgery, talk with your doctor before trying this stretch.",
"    <ol>",
"     <li>",
"      Sit securely toward the front of a sturdy, armless chair with your feet flat on the floor, shoulder-width apart.",
"     </li>",
"     <li>",
"      Slowly bend forward from your hips. Keep your back and neck straight.",
"     </li>",
"     <li>",
"      Slightly relax your neck and lower your chin. Slowly bend farther forward and slide your hands down your legs toward your shins. Stop when you feel a stretch or slight discomfort.",
"     </li>",
"     <li>",
"      Hold for 10 to 30 seconds.",
"     </li>",
"     <li>",
"      Straighten up slowly all the way to the starting position.",
"     </li>",
"     <li>",
"      Repeat at least three to five times.",
"     </li>",
"    </ol>",
"    As you progress, bend as far forward as you can and eventually touch your heels.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Go4Life. National Institute on Aging at the National Institutes of Health. Available at: file://go4life.niapublications.org (Accessed December 2, 2011).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
