var title_f35_63_36859="Approach to diagnostic testing for adenovirus";
var content_f35_63_36859=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F81573&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F81573&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approach to diagnostic testing for adenovirus based upon the clinical syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Suggested test(s) for adenovirus if diagnosis is necessary*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Upper respiratory illness",
"       </td>",
"       <td>",
"        Viral culture if available and/or viral antigen assay on respiratory specimen(s) (eg, nasopharyngeal swab or aspirate; throat swab or wash)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Epidemic keratoconjunctivitis",
"       </td>",
"       <td>",
"        Viral culture and/or adenovirus-specific antigen assay on conjunctival swab or scraping",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pneumonia",
"       </td>",
"       <td>",
"        Viral culture or PCR of nasopharyngeal aspirate or swab, throat swab, sputum, tracheal aspirate or BAL material",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged diarrhea in young children",
"       </td>",
"       <td>",
"        Adenovirus-specific antigen assay on stool specimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunocompromised hosts",
"       </td>",
"       <td>",
"        Viral culture or PCR of affected sites; quantitative PCR of blood; biopsy if clinically indicated",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PCR: polymerase chain reaction; BAL: bronchoalveolar lavage.",
"     <br>",
"      * If there is a potential outbreak or an individual patient has a severe manifestation (to make decisions about antiviral therapy, exclude other treatable infections, determine prognosis, and/or initiate infection control measures).",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       The appropriate diagnostic tests depend upon the severity and site(s) of infection, as well as the degree of immunocompromise.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
