var title_f36_10_37035="High chol treat PI";
var content_f36_10_37035=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F63578&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F63578&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    American Academy of Pediatrics recommendations on screening for and treatment of high cholesterol in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        When to perform screening tests*?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If family history of high cholesterol or early cardiovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If family history unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If child has risk factors (obesity, high blood pressure, medical illness that increases risk)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform testing every 3 to 5 years beginning at age 2 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        When to begin lifestyle changes (diet, exercise)?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At 1 year of age, begin reduced-fat (2 percent) milk for children at risk due to obesity or family history of cardiovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In children with elevated serum LDL-C, the recommended diet should have saturated fat &lt;7 percent, trans fat &lt;1 percent, dietary cholesterol &lt;200 mg/day, and fiber intake equal to child's age plus 5 g/day (up to 20 g/day at 15 years of age)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encourage physical activity to manage weight and treat high triglyceride levels and low HDL cholesterol levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        When to consider medication for high cholesterol?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider medication in children at least 8 years of age or older, if LDL &ge;190 mg/dL or &ge;160 mg/dL with positive family history or two additional risk factors or &ge;130 mg/dL with type 1 diabetes mellitus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Screening tests usually include blood tests for total cholesterol, HDL (high density lipoprotein), LDL (low density lipoprotein), and triglycerides, drawn before the first meal of the day.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Daniels SR, Greer FR. Lipid screening and cardiovascular health in childhood. Pediatrics 2008; 122:198.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
