var title_f36_15_37115="Clinically important anaerobes";
var content_f36_15_37115=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F61844&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F61844&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinically important and most frequently isolated anaerobes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gram negative bacteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bacteroides fragilis group: B. fragilis, B. thetaiotaomicron, B. distasonis, B. ovatus, B. vulgatus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pigmented Prevotella (formerly Bacteroides): P. intermedia, P. melaninogenica, P. corporis, P. denticola, P. loescheii, P. nigrescens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prevotella (other): P. bivia (formerly B. bivius), P. disiens (formerly B. disiens), P. oralis (formerly B. oralis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Porphyromonas asaccharolytica (formerly B. asaccharolyticus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fusobacterium: F. nucleatum, F. necrophorum, F. varium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilophila: B. wadsworthia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gram positive cocci",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peptostreptococcus: P. intermedius, P. micros, P. anaerobius, P. magnus, P. asaccharolyticus, P. prevotii",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gram positive, spore-forming bacilli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clostridium: C. perfringens, C. difficile, C. sporogenes, C. sordellii, C. septicum, C. tertium, C. ramosum, C. novyi, C. histolyticum, C. bifermentans, C. innocuum, C. tetani, C. botulinum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gram positive, non-spore-forming bacilli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Propionibacterium: P. acnes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eubacterium: E. lentum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bifidobacterium: B. dentium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Actinomyces: A. israelii, A. naeslundii, A. odontolyticus, A. viscosus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
