var title_f36_2_36907="Potential indications for using emergency contraception";
var content_f36_2_36907=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F51098&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F51098&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential indications for using emergency contraception",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        When no contraceptive was used during sexual intercourse within the previous 120 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        When there is a contraceptive failure or incorrect use of a contraceptive within the previous 120 hours, including:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Condom breakage, slippage, or incorrect use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Three or more 30 to 35 mcg ethinyl estradiol pills have been missed (or two or more 20 to 25 mcg pills)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Progestin-only pill (minipill) taken more than three hours late",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        More than two weeks late for injection of depot-medroxyprogesterone acetate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dislodgment, breakage, tearing, or early removal of a diaphragm or cervical cap",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dislodgment, delay in placing, or early removal of a contraceptive hormonal skin patch or vaginal ring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Failed coitus interruptus (eg, ejaculation in vagina or on external genitalia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Failure of a spermicide tablet or film to melt before sexual intercourse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Miscalculation of the periodic abstinence method or failure to abstain on fertile day of cycle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Expulsion of intrauterine contraception",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * We suggest the simpler recommendation that emergency contraception be used after two missed pills, regardless of dose.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: World Health Organization fact sheet No. 244 on Emergency Contraception. 2005.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
