" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pisiform fractures",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/22/37218/contributors\">",
