var title_f36_22_37228="Treatment of Whipples disease";
var content_f36_22_37228=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F65520&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F65520&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antimicrobial therapy in Whipple's disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Indication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Initial therapy*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        General infection",
"       </td>",
"       <td>",
"        <p>",
"         Ceftriaxone 2 g IV once daily",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        Penicillin G 2 million units IV every four hours",
"       </td>",
"       <td>",
"        Two weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endocarditis",
"       </td>",
"       <td>",
"        <p>",
"         Penicillin G 2 million units IV every four hours",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        Ceftriaxone 2 g IV once daily",
"       </td>",
"       <td>",
"        Four weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CNS disease",
"       </td>",
"       <td>",
"        <p>",
"         Ceftriaxone 2 g IV once daily",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        Penicillin G 4 million units IV every four hours",
"       </td>",
"       <td>",
"        Four weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        If ceftriaxone and penicillin allergic",
"       </td>",
"       <td>",
"        <p>",
"         Trimethoprim-sulfamethoxazole one DS tablet three times daily",
"        </p>",
"        <p>",
"         <strong>",
"          PLUS",
"         </strong>",
"        </p>",
"        Streptomycin 1 g IM daily",
"       </td>",
"       <td>",
"        Two to four weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Maintenance therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All infections",
"       </td>",
"       <td>",
"        Trimethoprim-sulfamethoxazole one DS tablet twice daily",
"       </td>",
"       <td>",
"        One year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        If sulfa allergic",
"       </td>",
"       <td>",
"        <p>",
"         Doxycycline 100 mg PO twice daily",
"        </p>",
"        <p>",
"         <strong>",
"          PLUS",
"         </strong>",
"        </p>",
"        Hydroxychloroquine 200 mg PO thrice daily",
"       </td>",
"       <td>",
"        One year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Therapy for relapse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Initial therapy*",
"       </td>",
"       <td>",
"        <p>",
"         Penicillin G 4 million units IV every 4 hours",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        Ceftriaxone 2 g IV twice daily",
"       </td>",
"       <td>",
"        Four weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maintenance therapy",
"       </td>",
"       <td>",
"        <p>",
"         Doxycycline 100 mg PO twice daily",
"         <strong>",
"          PLUS",
"         </strong>",
"         hydroxychloroquine 200 mg PO thrice daily",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        Trimethoprim-sulfamethoxazole one DS tablet twice daily for 1 year",
"       </td>",
"       <td>",
"        One year",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Initial therapy must be followed by maintenance therapy.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
