var title_f36_23_37243="Follic lymphoma int prog index";
var content_f36_23_37243=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F55987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F55987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Follicular lymphoma international prognostic index (FLIPI)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        &bull; Age &gt;60",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        &bull; Serum lactate dehydrogenase concentration above normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        &bull; Hemoglobin level &lt;12.0 g/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        &bull; Ann Arbor stage III or IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        &bull; Number of involved nodal areas &gt;4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"4\" rowspan=\"1\">",
"        One point is given for each of the",
"above characteristics present in the patient with follicular lymphoma",
"(FL), for a total score ranging from zero to five. When applied to an",
"international study of long-term survival in 4, 167 patients with FL",
"diagnosed between 1985 and 1992, the following three risk groups and",
"their corresponding 5- and 10-year overall survivals (OS) were, as",
"follows:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Score",
"       </td>",
"       <td class=\"subtitle2\">",
"        Risk Group",
"       </td>",
"       <td class=\"subtitle2\">",
"        5-yr OS, percent",
"       </td>",
"       <td class=\"subtitle2\">",
"        &nbsp;10-yr OS, percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0 to 1",
"       </td>",
"       <td>",
"        Low risk",
"       </td>",
"       <td>",
"        91",
"       </td>",
"       <td>",
"        71",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Intermediate risk",
"       </td>",
"       <td>",
"        78",
"       </td>",
"       <td>",
"        51",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 or more",
"       </td>",
"       <td>",
"        High risk",
"       </td>",
"       <td>",
"        52",
"       </td>",
"       <td>",
"        36",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Solal-Celigny, P, Roy, P, Colombat, P, et al. Follicular lymphoma international prognostic index. Blood 2004; 104:1258.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
