var title_f36_25_37275="NCI CTCAE hypertension";
var content_f36_25_37275=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F87347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F87347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    National Cancer Institute Common Terminology Criteria for hypertension (CTCAE v 4.0)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup width=\"90%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        1",
"       </td>",
"       <td>",
"        Prehypertension (systolic BP 120 to 139 mmHg or diastolic BP 80 to 89 mmHg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        2",
"       </td>",
"       <td>",
"        Stage 1 hypertension (systolic BP 140 to 159 mmHg or diastolic BP 90 to 99 mmHg); medical intervention indicated; recurrent or persistent (&ge;24 hours); symptomatic increase by &gt;20 mmHg (diastolic) or to &gt;140/90 mmHg if previously within normal limits; monotherapy indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        3",
"       </td>",
"       <td>",
"        Stage 2 hypertension (systolic BP &ge;160 mmHg or diastolic BP &ge;100 mmHg); medical intervention indicated; more than one drug or more intensive therapy than previously used indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        4",
"       </td>",
"       <td>",
"        Life-threatening consequences (eg, malignant hypertension, transient or permanent neurologic deficit, hypertensive crisis); urgent intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        5",
"       </td>",
"       <td>",
"        Death",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BP: blood pressure.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Common Terminology Criteria for Adverse Events, version 4.0, June 2010, National Institutes of Health, National Cancer Institute. Available at:",
"     <a href=\"file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf\" target=\"_blank\">",
"      file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf",
"     </a>",
"     (Accessed December 14, 2012).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
