var title_f36_28_37323="Clinical characteristics basilar-type migraine";
var content_f36_28_37323=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F73674&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F73674&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical characteristics of basilar-type migraine",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Always present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two or more basilar-type aura symptoms (ie, vertigo, dysarthria, tinnitus, diplopia, bilateral visual symptoms, bilateral paresthesia, decreased level of consciousness, hypacusis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visual aura characterized by positive features (ie, zigzag lines, flickering light) or negative features (ie, scotoma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        May be present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sensory aura characterized by positive features (ie, pins and needles) or negative features (ie, numbness)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aphasic aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Temporal characteristics of aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aura symptoms almost always develop gradually over &ge;5 minutes and/or aura symptoms occur in succession over &ge;5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duration 5 to 120 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A headache with migraine features begins after the onset of aura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patient characteristics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Basilar-type migraine onset occurs before age 50",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Most patients with basilar-type migraine also have attacks of migraine with typical aura and without basilar-type aura",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Kirchmann M, Thomsen LL, Olesen J. Basilar-type migraine: clinical, epidemiologic, and genetic features. Neurology 2006; 66:880.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
