" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Drugs with evidence (clinical plus presence of drug-dependent antibodies) for association with immune thrombocytopenia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Abciximab*",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acetaminophen",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amiodarone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ampicillin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carbamazepine*",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eptifibatide",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
