" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Vernal keratoconjunctivitis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/30/37352/contributors\">",
