var title_f36_35_37436="Impact of medications and methylxanthines on stress testing";
var content_f36_35_37436=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F53174&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F53174&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for withholding medications and methylxanthines when testing is performed for the diagnosis of coronary heart disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Medication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Considerations if withholding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beta blockers",
"       </td>",
"       <td>",
"        Withhold for 48 hours",
"       </td>",
"       <td>",
"        Hypertension and arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium channel blockers",
"       </td>",
"       <td>",
"        Withhold for 48 hours",
"       </td>",
"       <td>",
"        Hypertension and arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nitrates",
"       </td>",
"       <td>",
"        Withhold for 48 hours",
"       </td>",
"       <td>",
"        Hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ACEI",
"       </td>",
"       <td>",
"        Continue",
"       </td>",
"       <td>",
"        Hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ARB",
"       </td>",
"       <td>",
"        Continue",
"       </td>",
"       <td>",
"        Hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amiodarone",
"       </td>",
"       <td>",
"        Continue as withholding not practical due to extended washout period",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        L-Arginine",
"       </td>",
"       <td>",
"        Withhold prior to testing",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Statins",
"       </td>",
"       <td>",
"        Continue",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Methylxanthines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Caffeine containing foods and beverages",
"       </td>",
"       <td class=\"sublist_other\">",
"        Withhold for 12 hours prior to vasodilator stress testing",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Aminophylline",
"       </td>",
"       <td class=\"sublist_other\">",
"        Withhold for 24 hours prior to vasodilator stress testing",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dipyridamole",
"       </td>",
"       <td class=\"sublist_other\">",
"        Withhold for 48 hours prior to vasodilator stress testing",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Digoxin",
"       </td>",
"       <td>",
"        Continue as withholding not practical due to extended washout period",
"       </td>",
"       <td>",
"        Perform concomitant imaging as initial approach or if results felt to be falsely positive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Estrogen",
"       </td>",
"       <td>",
"        Continue as withholding not practical due to extended washout period",
"       </td>",
"       <td>",
"        Perform concomitant imaging as initial approach or if results felt to be falsely positive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diuretics",
"       </td>",
"       <td>",
"        Continue",
"       </td>",
"       <td>",
"        Replete hypokalemia prior to stress testing",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ACEI: angiotensin converting enzyme inhibitor; ARB: angiotensin receptor blocker.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
