var title_f36_36_37451="Lacunar syndromes";
var content_f36_36_37451=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F67341&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F67341&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classical lacunar syndromes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Lacunar syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Location",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Positive predictive value",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pure motor hemiparesis",
"       </td>",
"       <td>",
"        Internal capsule, corona radiata, basal pons, medial medulla",
"       </td>",
"       <td>",
"        Unilateral paralysis of face, arm and leg, no sensory signs, dysarthria and dysphagia may be present",
"       </td>",
"       <td>",
"        52-85 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pure sensory syndrome",
"       </td>",
"       <td>",
"        Thalamus, pontine tegmentum, corona radiata",
"       </td>",
"       <td>",
"        Unilateral numbness of face, arm and leg without motor deficit",
"       </td>",
"       <td>",
"        95-100 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ataxic hemiparesis",
"       </td>",
"       <td>",
"        Internal capsule-corona radiata, basal pons, thalamus",
"       </td>",
"       <td>",
"        Unilateral weakness and limb ataxia",
"       </td>",
"       <td>",
"        59-95 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sensorimotor syndrome",
"       </td>",
"       <td>",
"        Thalamocapsular, maybe basal pons or lateral medulla",
"       </td>",
"       <td>",
"        Hemiparesis or hemiplegia of face, arm and leg with ipsilateral sensory impairment",
"       </td>",
"       <td>",
"        51-87 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dysarthria-clumsy hand syndrome",
"       </td>",
"       <td>",
"        Basal pons, internal capsule- corona radiata",
"       </td>",
"       <td>",
"        Unilateral facial weakness, dysarthria and dysphagia, with mild hand weakness and clumsiness",
"       </td>",
"       <td>",
"        About 96 percent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
