var title_f36_36_37452="Monitoring in diabetes mellitus";
var content_f36_36_37452=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F63002&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F63002&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Monitoring in patients with diabetes mellitus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Intervention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency",
"       </td>",
"       <td class=\"subtitle1\">",
"        Notes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        History and physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Smoking cessation counseling",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"       <td>",
"        For smokers only",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Blood pressure",
"       </td>",
"       <td>",
"        Every visit",
"       </td>",
"       <td>",
"        Goal &lt;140/80",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dilated eye examination",
"       </td>",
"       <td>",
"        Annually*",
"       </td>",
"       <td>",
"        Begin at onset of type 2 diabetes, three to five years after onset of type 1 diabetes. Examine more than annually if significant retinopathy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Foot examination",
"       </td>",
"       <td>",
"        Annually",
"       </td>",
"       <td>",
"        Every visit if peripheral vascular disease or neuropathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Laboratory studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fasting serum lipid profile",
"       </td>",
"       <td>",
"        Annually",
"       </td>",
"       <td>",
"        May obtain every two years if profile is low risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A1C",
"       </td>",
"       <td>",
"        Every three to six months",
"       </td>",
"       <td>",
"        Goal &lt;7 percent (may be lower or higher in selected patients)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Urinary albumin-to-creatinine ratio",
"       </td>",
"       <td>",
"        Annually",
"       </td>",
"       <td>",
"        Begin three to five years after onset of type 1 diabetes; protein excretion and serum creatinine should also be monitored if persistent albuminuria is present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Serum creatinine",
"       </td>",
"       <td>",
"        Initially, as indicated",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Vaccinations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumococcus",
"       </td>",
"       <td>",
"        One time",
"       </td>",
"       <td>",
"        Patients over age 65 need a second dose if vaccine was received &ge;5 years previously and age was &lt;65 at time of vaccination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Influenza",
"       </td>",
"       <td>",
"        Annually",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis B",
"       </td>",
"       <td>",
"        Three dose series",
"       </td>",
"       <td>",
"        Administer to unvaccinated adults who are ages 19 to 59 years. For older patients, administer based upon risk of acquiring hepatitis B and likelihood of an adequate immune response to vaccination.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Education, self management review",
"       </td>",
"       <td>",
"        Annually",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Less frequent screening (every two to three years) may be appropriate for some patients.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
