var title_f36_41_37532="Diagnostic criteria for functional dyspepsia";
var content_f36_41_37532=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F87623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F87623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for functional dyspepsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic criteria for functional dyspepsia*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         One or more",
"        </strong>",
"        of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. Bothersome postprandial fullness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. Early satiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        3. Epigastric pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        4. Epigastric burning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         AND",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        No evidence of structural disease (including at upper endoscopy) that is likely to explain the symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic criteria for postprandial distress syndrome*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Must include",
"        <strong>",
"         one or both",
"        </strong>",
"        of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. Bothersome postprandial fullness, occurring after ordinary sized meals, at least several times per week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. Early satiation that prevents finishing a regular meal, at least several times a week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Supportive criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. Upper abdominal bloating or postprandial nausea or excessive belching can be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. Epigastric pain syndrome may coexist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic criteria for epigastric pain syndrome*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Must include",
"        <strong>",
"         all",
"        </strong>",
"        of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. Pain or burning localized to other abdominal or chest regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. The pain is intermittent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        3. Not generalized or localized to other abdominal or chest regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        4. Not relieved by defecation or passage of flatus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        5. Not fulfilling criteria for gallbladder and sphincter of Oddi disorders",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Supportive criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1. The pain may be of a burning quality but without a retrosternal component",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2. The pain is commonly induced or relieved by ingestion of a meal but may occur while fasting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        3. Postprandial distress syndrome may coexist",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Criteria must be&nbsp;fulfilled for the last three months with symptom onset at least six months before diagnosis.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Tack J, Talley NJ, Camilleri M, et al. Functional gastroduodenal disorders. Gastroenterology 2006; 130:1466. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f36_41_37532=[""].join("\n");
var outline_f36_41_37532=null;
var title_f36_41_37533="Lung cancer TMN staging 7th edition";
var content_f36_41_37533=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F80099&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F80099&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging system for lung cancer (7th edition)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Primary tumor (T)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        T1",
"       </td>",
"       <td class=\"sublist_other_start\" colspan=\"3\">",
"        Tumor &le;3 cm diameter, surrounded by lung or visceral pleura, without invasion more proximal than lobar bronchus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        T1a",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Tumor &le;2 cm in diameter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        T1b",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Tumor &gt;2 cm but &le;3 cm in diameter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        T2",
"       </td>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Tumor &gt;3 cm but &le;7 cm, or tumor with any of the following features:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Involves main bronchus, &ge;2 cm distal to carina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Invades visceral pleura",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Associated with atelectasis or obstructive pneumonitis that extends to the hilar region but does not involve the entire lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        T2a",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Tumor &gt;3 cm but &le;5 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        T2b",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Tumor &gt;5 cm but &le;7 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        T3",
"       </td>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Tumor &gt;7 cm or any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Directly invades any of the following: chest wall, diaphragm, phrenic nerve, mediastinal pleura, parietal pericardium, main bronchus &lt;2 cm from carina (without involvement of carina)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Atelectasis or obstructive pneumonitis of the entire lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"3\">",
"        Separate tumor nodules in the same lobe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T4",
"       </td>",
"       <td colspan=\"3\">",
"        Tumor of any size that invades the mediastinum, heart, great vessels, trachea, recurrent laryngeal nerve, esophagus, vertebral body, carina, or with separate tumor nodules in a different ipsilateral lobe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Regional lymph nodes (N)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N0",
"       </td>",
"       <td colspan=\"3\">",
"        No regional lymph node metastases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N1",
"       </td>",
"       <td colspan=\"3\">",
"        Metastasis in ipsilateral peribronchial and/or ipsilateral hilar lymph nodes and intrapulmonary nodes, including involvement by direct extension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N2",
"       </td>",
"       <td colspan=\"3\">",
"        Metastasis in ipsilateral mediastinal and/or subcarinal lymph node(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N3",
"       </td>",
"       <td colspan=\"3\">",
"        Metastasis in contralateral mediastinal, contralateral hilar, ipsilateral or contralateral scalene, or supraclavicular lymph node(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Distant metastasis (M)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        M0",
"       </td>",
"       <td colspan=\"3\">",
"        No distant metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        M1",
"       </td>",
"       <td class=\"sublist_other_start\" colspan=\"3\">",
"        Distant metastasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        M1a",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Separate tumor nodule(s) in a contralateral lobe; tumor with pleural nodules or malignant pleural or pericardial effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        M1b",
"       </td>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        Distant metastasis (in extrathoracic organs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"4\">",
"        Stage groupings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IA",
"       </td>",
"       <td>",
"        T1a-T1b",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IB",
"       </td>",
"       <td>",
"        T2a",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Stage IIA",
"       </td>",
"       <td>",
"        T1a,T1b,T2a",
"       </td>",
"       <td>",
"        N1",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T2b",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Stage IIB",
"       </td>",
"       <td>",
"        T2b",
"       </td>",
"       <td>",
"        N1",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T3",
"       </td>",
"       <td>",
"        N0",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Stage IIIA",
"       </td>",
"       <td>",
"        T1a,T1b,T2a,T2b",
"       </td>",
"       <td>",
"        N2",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T3",
"       </td>",
"       <td>",
"        N1,N2",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        T4",
"       </td>",
"       <td>",
"        N0,N1",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Stage IIIB",
"       </td>",
"       <td>",
"        T4",
"       </td>",
"       <td>",
"        N2",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any T",
"       </td>",
"       <td>",
"        N3",
"       </td>",
"       <td>",
"        M0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage IV",
"       </td>",
"       <td>",
"        Any T",
"       </td>",
"       <td>",
"        Any N",
"       </td>",
"       <td>",
"        M1a or M1b",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Goldstraw P, Crowley J, Chansky K, et al. The IASLC Lung Cancer Staging Project: Proposals for the revision of the TNM stage groups in the forthcoming (seventh) edition of the TNM classification of malignant tumours. J Thorac Oncol 2007; 2:706.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
