var title_f36_45_37595="International neuroblastoma staging system";
var content_f36_45_37595=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F73974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F73974&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    International neuroblastoma staging system",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Localized tumor with complete gross excision, with or without microscopic residual disease; representative ipsilateral lymph nodes negative for tumor microscopically (nodes attached to and removed with the primary tumor may be positive)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2A",
"       </td>",
"       <td>",
"        Localized tumor with incomplete gross excision; representative ipsilateral nonadherent lymph nodes negative for tumor microscopically",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2B",
"       </td>",
"       <td>",
"        Localized tumor with or without complete gross excision; with ipsilateral nonadherent lymph nodes positive for tumor. Enlarged contralateral lymph nodes must be negative microscopically.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Unresectable unilateral tumor infiltrating across the midline, with or without regional lymph node involvement; or localized unilateral tumor with contralateral regional lymph node involvement; or midline tumor with bilateral extension by infiltration (unresectable) or by lymph node involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Any primary tumor with dissemination to distant lymph nodes, bone, bone marrow, liver, skin and/or other organs (except as defined for stage 4S)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4S",
"       </td>",
"       <td>",
"        Localized primary tumor (as defined for stage 1, 2A or 2B), with dissemination limited to skin, liver, and/or bone marrow (limited to infants &lt;1 year of age)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Brodeur GM, Pritchard J, Berthold F, et al. Revisions of the international criteria for neuroblastoma diagnosis, staging, and response to treatment. J Clin Oncol 1993; 11:1466. Copyright &copy; 1993 American Society of Clinical Oncology.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
