var script_f36_47_37630=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function BodyMassIndexPatient_Metric_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.BodyMassIndexPatient_Metric_form){",
"",
"",
"doCalc = true;",
"if (Height_param.value.indexOf(',') >= 0){ Height_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Height_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Height_unit.options[Height_unit.selectedIndex].value.split('|');",
"Height = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Weight_param.value.indexOf(',') >= 0){ Weight_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Weight_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Weight_unit.options[Weight_unit.selectedIndex].value.split('|');",
"Weight = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = 1;",
"BMI =  Weight / power(Height/100, 2);",
"",
"if (doCalc) BMI_param.value = fixDP(BMI, dp);",
"",
"",
"",
"",
"if (doCalc){",
"",
"rrclr();",
"if (BMI < 18.5){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((BMI >= 18.5) && (BMI < 25)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if ((BMI >= 25) && (BMI < 30)){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"if (BMI >= 30){ document.getElementById('rr1_4').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.BodyMassIndexPatient_Metric_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.BodyMassIndexPatient_Metric_form){",
"",
"BMI_param.value = '';",
"",
"rrclr();",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
