var title_f36_50_37676="Description of genital ulcer I";
var content_f36_50_37676=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F60432&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F60432&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of sexually transmitted genital ulcers",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Primary syphilis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Genital herpes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chancroid",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lymphogranuloma venereum",
"       </td>",
"       <td class=\"subtitle1\">",
"        Donovanosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Etiology",
"       </td>",
"       <td>",
"        Treponema pallidum",
"       </td>",
"       <td>",
"        Herpes simplex",
"       </td>",
"       <td>",
"        Haemophilus ducreyi",
"       </td>",
"       <td>",
"        Chlamydia trachomatis",
"       </td>",
"       <td>",
"        Calymmatobacterium granulomatis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Incubation period",
"       </td>",
"       <td>",
"        9 - 90 days; average 2 - 4 weeks",
"       </td>",
"       <td>",
"        2 - 7 days",
"       </td>",
"       <td>",
"        Range 1 - 35 days; average 3 - 7 days",
"       </td>",
"       <td>",
"        3 days - 3 weeks; average 10 - 14 days",
"       </td>",
"       <td>",
"        Precise data unavailable; probably a few days to several months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Number of lesions",
"       </td>",
"       <td>",
"        Usually single lesion, but multiple lesions may occur",
"       </td>",
"       <td>",
"        Multiple; may coalesce; more lesions appear in primary episodes than in recurrences",
"       </td>",
"       <td>",
"        Usually 1 -3, but multiple lesions may occur",
"       </td>",
"       <td>",
"        Usually single",
"       </td>",
"       <td>",
"        Single or multiple",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Appearance of genital ulcers",
"       </td>",
"       <td>",
"        Sharply demarcated round or oval ulcer with slightly elevated edges; may be irregular or symmetrical (\"kissing chancre\").",
"       </td>",
"       <td>",
"        Small superficial grouped",
"vesicles and/or erosions; lesions may coalesce, forming bullae or large",
"areas of ulceration; lesions have irregular borders.",
"       </td>",
"       <td>",
"        Deep, sharply demarcated ulcer; irregular ragged undermined edge; ranges in diameter from a few ml to 2 cm.",
"       </td>",
"       <td>",
"        Papule, pustule, vesicle, or ulcer; discrete and transient; frequently overlooked.",
"       </td>",
"       <td>",
"        Sharply defined irregular ulcerations or hypertrophic, verrucous, necrotic, or cicatrical granulomas.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Base",
"       </td>",
"       <td>",
"        Red, smooth and shiny, or crusted; serous exudate occurs when squeezed.",
"       </td>",
"       <td>",
"        Bright, red, and smooth.",
"       </td>",
"       <td>",
"        Rough, uneven, yellow to gray in color.",
"       </td>",
"       <td>",
"        Variable.",
"       </td>",
"       <td>",
"        Usually friable, rough, beefy granulations; can be necrotic, verrucous, or cicatrical.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Induration",
"       </td>",
"       <td>",
"        Firm; does not change shape with pressure.",
"       </td>",
"       <td>",
"        None.",
"       </td>",
"       <td>",
"        Soft; changes shape with pressure.",
"       </td>",
"       <td>",
"        None.",
"       </td>",
"       <td>",
"        Firm granulation tissue.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from Martin DH, Mroczkowski TF: Sexually Transmitted Diseases. In: The Skin and Infection: A Color Atlas and Text. C. V. Sanders and LT Nesbitt, eds. Baltimore, Maryland: Williams &amp; Wilkins, 1995, p. 95.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
